import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { forkJoin } from 'rxjs';
import { PokeApiService } from 'src/app/service/poke-api.service';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.scss'],
})
export class DetailsComponent {
  private urlPokemon: string = 'https://pokeapi.co/api/v2/pokemon';
  private urlName: string = 'https://pokeapi.co/api/v2/pokemon-species';
  public pokemon: any;

  ngOnInit(): void {
    this.getPokemon;
  }

  constructor(
    private activatedRoute: ActivatedRoute,
    private pokeApiService: PokeApiService
  ) {}

  get getPokemon() {
    const id = this.activatedRoute.snapshot.params['id'];
    const pokemon = this.pokeApiService.apiGetPokemons(
      `${this.urlPokemon}/${id}`
    );
    const name = this.pokeApiService.apiGetPokemons(`${this.urlName}/${id}`);

    return forkJoin([pokemon, name]).subscribe((res) => {
      this.pokemon = res;
    });
  }
}
<app-poke-header></app-poke-header>

<main>
  <section class="pokemon__names">
    <h1>{{ pokemon[0].name }}</h1>
    <h2>{{ pokemon[1].names[0].name }}</h2>
  </section>
  <section class="pokemon__status">
    <img
      [src]="pokemon[0].sprites.other.dream_world.front_default"
      [alt]="pokemon[0].name"
    />
    <article class="pokemon__status__statistcs">
      <section>
        <h3>Estatísticas</h3>
        <ol>
          <li *ngFor="let value of pokemon[0].stats">
            {{ value.stat.name }}
            <strong>
              {{ value.base_stat }}
            </strong>
          </li>
        </ol>
      </section>
      <a [routerLink]="['']" class="btn btn__secondary">Voltar</a>
    </article>
  </section>
</main>
